<?class Mess {	const ERROR_TXT = 'Do&scaron;lo k z&aacute;va&#382;nej chybe, sk&uacute;ste pros&iacute;m nesk&ocirc;r.';		public static function Warning($sprava = self::ERROR_TXT) {		return "<table width='280' height='120' align='center'>				<tr>					<td align='center' class='ster_info_gray'>						<img src='".ROOT."web2/images/theme/warning_nenajdene.png' alt=''/>						<br>".$sprava."					</td>				</tr>			</table>";	} 	public static function Error($error = self::ERROR_TXT) {		return self::Tip('<strong>Error:</strong> '.$error);	} 	public static function Tip($sprava = self::ERROR_TXT, $type='home') {		return '		<div class="ui-widget">			<div class="webtip ui-state-highlight ui-corner-all"> 				<span class="ui-icon ui-icon-'.$type.'"/></span>'.$sprava.'			</div> 		</div>';	}	public static function Alert($header, $sprava, $sirka=140) {			static $i; $i++;		// Error dialog					echo '<div id="dialog_error_', $i, '" title="', $header, '">', $sprava, '</div>        <script type="text/javascript">$(function() {            $("#dialog_error_'.$i.'").dialog({                bigframe: true,                height: '.$sirka.',                modal: true            });        });</script>';	}}?>