<?phpclass GaleriaAlbum extends DBTable{ 	public function GaleriaAlbum($sql='') {		if($sql) {			$this->__data = DB::Query($sql);		}		}	public static function GetPrivate($id = 0) {		$povolene = array(0);		// Priatel,.. 		if($id > 0 and User::Logged()) {			if(User::$m->is_friend($id)) {				$povolene[] = 2;			}		}		return implode(',', $povolene);  	}	public static function CheckPrivate2($data) {		switch($data) {			// + je zaheslovany  			case 1 : return 'Tento album je s?kromn?';			// + je len pre priatelov  			case 2 : return 'Tento album je s?kromn?';			// + aj aj 			case 3 : return 'Tento album je s?kromn?';     			// + je len pre majitela			case 4 : return 'Tento album je s?kromn?';			default : return true; 		}		return true;	}	public function CheckPrivate() {		return self::CheckPrivate2($this->visible);	}}?>